onOptionChange:
{{

  table_results.setData([]);

  if (!dropdown_query_selector.selectedOptionValue) {
  panel_query_params.setVisibility(false);
  } else {
    // Show relevant parameters for selected query
    QueryHandler.showParams();
  }

  // Update description
  text_query_description.setText(
    QueryDescriptions.getDescription(dropdown_query_selector.selectedOptionValue)
  );
}}
